<?php//========================//if(INCLUDED!==true) {	echo "Not Included!"; exit;}//=======================//function saveConfig() {	$cfg = new Config();			// Store New/Changed config items	writeLog("Saving Config...");	foreach ($_POST as $item => $val) {		$key = explode('__', $item);		if ($key[0] == 'cfg') {			writeLog(" -> Found Key: '".$key[1]."' => '".((is_array($cfg->get($key[1])))?str_replace("\r\n", ",",$val):$val)."' (Old: ".((is_array($cfg->get($key[1])))?implode(',',$cfg->get($key[1])):$cfg->get($key[1])).")...");			$cfg->set($key[1],$val);		}	}	$cfg->Save();	output_message('notice','Finished! Config Successfully Updated.');}?>